const inputSearch=document.querySelector("#input-search"),filterButton=document.querySelector(".filter__button"),filterList=document.querySelector(".filter__list"),filterItems=document.querySelectorAll(".filter__item"),countriesSection=document.querySelector(".countries");console.log(filterList);const BASE_URL="https://restcountries.eu/rest/v2";let region="asia";async function getCountries(t){const e=await fetch(BASE_URL+"/region/"+t);showCountryData(await e.json(),t)}function showCountryData(t,e){t.forEach((t=>{let e=document.createElement("a");e.href=`country.html?name=${t.alpha2Code}`,e.classList.add("country"),e.innerHTML=`\n                        <div class="country__flag"><img src="${t.flag}" alt="Country Flag"></div>\n\n                        <div class="country__info">\n                        <h2 class="country__name country-name">${t.name}</h2>\n                        <p class="country__population country-info">\n                            Population: <span class="light-text">${t.population}</span>\n                        </p>\n                        <p class="country__region country-info">\n                            Region: <span class="light-text">${t.region}</span>\n                        </p>\n                        <p class="country__capital country-info">\n                            Capital: <span class="light-text">${t.capital}</span>\n                        </p>\n                        </div>`,countriesSection.appendChild(e)}));showByFilter(countriesSection.querySelectorAll(".country"),e)}function showByFilter(t,e){t.forEach((t=>{const n=t.querySelector(".country__region span");if(n.textContent.toLowerCase()!==e){n.parentElement.parentElement.parentElement.style.display="none"}}))}window.addEventListener("DOMContentLoaded",(()=>{getCountries(region)})),filterButton.addEventListener("click",(()=>{document.querySelector("#arrow").classList.toggle("fa-chevron-up"),filterList.classList.toggle("show-list")})),inputSearch.addEventListener("keyup",(t=>{const e=document.querySelectorAll(".country"),n=t.target.value.toLocaleLowerCase();e.forEach((t=>{const e=t.querySelector(".country__name"),o=e.textContent.toLocaleLowerCase(),r=e.parentElement.parentElement;-1!==o.indexOf(n)?r.style.display="block":r.style.display="none"}))})),filterItems.forEach((t=>{t.addEventListener("click",(t=>{let e=t.currentTarget.textContent.toLowerCase();region=e,getCountries(region)}))}));