const detailsSection=document.querySelector(".country-details"),query=new URLSearchParams(window.location.search),params=query.get("name"),url="https://restcountries.eu/rest/v2/alpha/";async function getCoutryData(){const n=await fetch(url+params);showCountryDetails(await n.json())}function showCountryDetails(n){detailsSection.innerHTML=`<div class="country-details__flag"><img src="${n.flag}" alt="Country Flag"></div>\n\n    <div class="country-details__main">\n      <h2 class="country-name country-details--name">${n.name}</h2>\n\n      <div class="country-details--top">\n        <p class="country-info">\n          Native Name: <span class="light-text">${n.nativeName}</span>\n        </p>\n        <p class="country-info">\n          Population: <span class="light-text">${n.population}</span>\n        </p>\n        <p class="country-info">\n          Region: <span class="light-text">${n.region}</span>\n        </p>\n        <p class="country-info">\n          Subregion: <span class="light-text">${n.subregion}</span>\n        </p>\n        <p class="country-info">\n          Capital: <span class="light-text">${n.capital}</span>\n        </p>\n      </div>\n\n      <div class="country-details--bottom">\n        <p class="country-info">\n          Top Level Domain: <span class="light-text">${n.topLevelDomain[0]}</span>\n        </p>\n        <p class="country-info">\n          Currencies: <span class="light-text">${n.currencies[0].name}</span>\n        </p>\n        <p class="country-info">\n          Languages: <span class="light-text">\n            ${n.languages.map((n=>n.name))}\n          </span>\n        </p>\n      </div>\n\n      <div class="country-details--border">\n        <p class="country-info">Border Countries:</p>\n        <div class="buttons">        \n          ${n.borders.map((n=>`<a href="country.html?name=${n}" class="country-button">\n                  ${n}\n                </a>`)).join(" ")}\n        </div>\n      </div>\n    </div>`}console.log(url+params),window.addEventListener("DOMContentLoaded",(n=>{getCoutryData()}));